CREATE DATABASE IF NOT EXISTS airline_db;
USE airline_db;

CREATE TABLE IF NOT EXISTS flights (
    flight_no VARCHAR(10) PRIMARY KEY,
    departure_city VARCHAR(50) NOT NULL,
    destination_city VARCHAR(50) NOT NULL,
    departure_time VARCHAR(10) NOT NULL,
    arrival_time VARCHAR(10) NOT NULL,
    duration VARCHAR(10) NOT NULL,
    price DECIMAL(10,2) NOT NULL,
    status VARCHAR(20) NOT NULL
);


CREATE TABLE IF NOT EXISTS passengers (
    passenger_id VARCHAR(10) PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    email VARCHAR(100) NOT NULL,
    phone VARCHAR(20) NOT NULL,
    flight_no VARCHAR(10) NOT NULL,
    departure_city VARCHAR(50) NOT NULL,
    destination_city VARCHAR(50) NOT NULL,
    boarding_pass VARCHAR(20) NOT NULL,
    seat_no VARCHAR(10) NOT NULL,
    status VARCHAR(20) NOT NULL,
    FOREIGN KEY (flight_no) REFERENCES flights(flight_no)
);


CREATE TABLE IF NOT EXISTS tickets (
    ticket_number VARCHAR(20) PRIMARY KEY,
    departure_city VARCHAR(50) NOT NULL,
    destination_city VARCHAR(50) NOT NULL,
    departure_date DATE NOT NULL,
    trip_type ENUM('One Way', 'Return') NOT NULL,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    nationality VARCHAR(50) NOT NULL,
    passport_number VARCHAR(20) NOT NULL,
    travel_class ENUM('Economy', 'Business', 'First Class') NOT NULL,
    issue_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);


CREATE TABLE IF NOT EXISTS feedback (
    id INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    email VARCHAR(100) NOT NULL,
    topic VARCHAR(100) NOT NULL,
    feedback TEXT NOT NULL,
    suggestions TEXT NOT NULL
);


CREATE TABLE IF NOT EXISTS Admins (
    username VARCHAR(50) PRIMARY KEY,
    password VARCHAR(50) NOT NULL,
    full_name VARCHAR(100) NOT NULL
);

