INSERT INTO feedback (name, email, topic, feedback, suggestions) VALUES
('John Smith', 'john.smith@email.com', 'Service Quality', 'The service was below expectations and needs improvement.', 'Please train staff better and improve response times.'),
('Sarah Johnson', 'sarah.j@email.com', 'Product Issue', 'Product arrived damaged and packaging was poor.', 'Use better packaging materials and handle with care.'),
('Mike Davis', 'mike.davis@email.com', 'Billing Problem', 'Incorrect charges on my bill for services not used.', 'Review billing system and provide clear itemized bills.'),
('Emily Brown', 'emily.brown@email.com', 'Customer Support', 'Support team was unhelpful and took too long to respond.', 'Hire more support staff and provide better training.'),
('David Wilson', 'david.w@email.com', 'Website Issues', 'Website is slow and difficult to navigate.', 'Improve website speed and user interface design.'),
('Ali Ahmed Khan', 'ali.khan@gmail.com', 'Flight Delay', 'My flight from Karachi to Dubai was delayed by 3 hours without proper notification. Very disappointing experience.', 'Please implement better communication system for delays and provide compensation for extended delays.'),
('Fatima Siddiqui', 'fatima.siddiqui@yahoo.com', 'Baggage Handling', 'My luggage was damaged during the flight from Islamabad to London. The zipper was broken and items were missing.', 'Improve baggage handling procedures and provide insurance coverage for damaged items.'),
('Muhammad Hassan', 'm.hassan@hotmail.com', 'Seat Comfort', 'The seats in economy class are too cramped and uncomfortable for long flights. My back was aching throughout the journey.', 'Consider upgrading seat design and providing more legroom, especially for international flights.'),
('Ayesha Malik', 'ayesha.malik@gmail.com', 'Food Quality', 'The meal served on the flight was cold and tasteless. Expected much better quality from your airline.', 'Partner with better catering services and ensure meals are served hot and fresh.'),
('Usman Ali', 'usman.ali@outlook.com', 'Check-in Process', 'Online check-in system keeps crashing and airport check-in queues are extremely long. Very frustrating experience.', 'Upgrade your IT systems and open more check-in counters during peak hours.'),
('Zainab Sheikh', 'zainab.sheikh@gmail.com', 'Staff Behavior', 'The cabin crew was rude and unprofessional during my flight from Lahore to Istanbul. They ignored passenger requests.', 'Provide comprehensive customer service training to all staff members and implement feedback monitoring.'),
('Tariq Mahmood', 'tariq.mahmood@yahoo.com', 'Booking System', 'Your online booking system charged my card twice for the same ticket. Had to wait weeks for refund.', 'Fix payment gateway issues and implement real-time transaction monitoring to prevent double charging.'),
('Nadia Iqbal', 'nadia.iqbal@gmail.com', 'Airport Lounge', 'The business class lounge in Karachi airport is overcrowded and lacks basic amenities like clean restrooms.', 'Expand lounge facilities and ensure regular maintenance and cleaning of all areas.'),
('Shahid Afridi', 'shahid.afridi@hotmail.com', 'Flight Cancellation', 'My connecting flight was cancelled without prior notice, causing me to miss an important business meeting.', 'Implement proactive communication for cancellations and provide alternative arrangements immediately.'),
('Rabia Nawaz', 'rabia.nawaz@gmail.com', 'Entertainment System', 'The in-flight entertainment system was not working throughout the 8-hour flight. Very boring journey.', 'Regular maintenance of entertainment systems and provide backup options like magazines or WiFi.'),
('Imran Qureshi', 'imran.qureshi@yahoo.com', 'Pricing Issues', 'Ticket prices fluctuate too much even within the same day. Lack of transparent pricing policy.', 'Implement fair and transparent pricing strategy with clear explanation of price variations.'),
('Hina Baloch', 'hina.baloch@gmail.com', 'Customer Service', 'Called customer service multiple times but no one answered. When finally connected, the agent was not helpful.', 'Increase customer service staff and provide them with proper training and authority to resolve issues.'),
('Waseem Akram', 'waseem.akram@hotmail.com', 'Refund Policy', 'Very complicated and lengthy refund process. Took 2 months to get my money back for a cancelled flight.', 'Simplify refund procedures and process refunds within 7-10 business days maximum.'),
('Sana Hashmi', 'sana.hashmi@gmail.com', 'Mobile App', 'Your mobile app crashes frequently and is very slow. Difficult to manage bookings through the app.', 'Develop a new mobile app with better performance and user-friendly interface.'),
('Bilal Shah', 'bilal.shah@yahoo.com', 'Loyalty Program', 'The frequent flyer program benefits are minimal and difficult to redeem. Not worth participating.', 'Enhance loyalty program with better rewards and easier redemption process.'),
('Farah Khan', 'farah.khan@gmail.com', 'Ground Services', 'Airport ground staff was unprofessional and provided incorrect information about gate changes.', 'Train ground staff properly and ensure they have updated information at all times.'),
('Kamran Akmal', 'kamran.akmal@hotmail.com', 'WiFi Service', 'In-flight WiFi is extremely expensive and very slow. Not worth the money charged.', 'Provide affordable and high-speed WiFi options for passengers.'),
('Mahira Butt', 'mahira.butt@gmail.com', 'Special Assistance', 'Requested wheelchair assistance for elderly parent but service was delayed and unprofessional.', 'Improve special assistance services and train staff to handle elderly and disabled passengers with care.'),
('Fawad Chaudhry', 'fawad.chaudhry@yahoo.com', 'Seat Selection', 'Paid extra for preferred seat but was moved to a different seat without notification or refund.', 'Honor seat selections and provide refunds or alternatives if changes are necessary.'),
('Aliya Bhutto', 'aliya.bhutto@gmail.com', 'Cleanliness', 'The aircraft was dirty with food particles on seats and floors. Restrooms were not properly cleaned.', 'Implement thorough cleaning procedures between flights and regular maintenance checks.'),
('Shoaib Malik', 'shoaib.malik@hotmail.com', 'Temperature Control', 'The cabin temperature was too cold throughout the flight and crew refused to adjust it despite multiple requests.', 'Ensure proper temperature control and train crew to address passenger comfort concerns.'),
('Kiran Dar', 'kiran.dar@gmail.com', 'Boarding Process', 'Boarding was chaotic and disorganized. No clear announcements and passengers were confused about zones.', 'Implement organized boarding procedures with clear announcements and proper queue management.'),
('Azhar Mahmood', 'azhar.mahmood@yahoo.com', 'Cargo Services', 'My cargo shipment was delayed by a week without any tracking updates or communication from your end.', 'Provide real-time tracking for cargo and proactive communication about any delays.'),
('Saima Wazed', 'saima.wazed@gmail.com', 'Travel Insurance', 'The travel insurance offered is overpriced and has too many exclusions. Not comprehensive at all.', 'Partner with better insurance providers and offer comprehensive coverage at reasonable rates.'),
('Yasir Shah', 'yasir.shah@hotmail.com', 'Airport Facilities', 'Islamabad airport facilities managed by your airline are inadequate. Long queues and insufficient seating.', 'Improve airport infrastructure and provide adequate facilities for passenger comfort.'),
('Ahmed Hassan', 'ahmed.hassan@email.ae', 'Service Quality', 'Excellent service from Dubai to Karachi. Cabin crew was very helpful and professional throughout the journey.', 'Continue maintaining high service standards and consider expanding routes in the Middle East.'),
('Raj Sharma', 'raj.sharma@gmail.com', 'Food Options', 'Limited vegetarian options on the Delhi to Lahore flight. Need more variety for different dietary requirements.', 'Expand meal options to cater to various dietary restrictions and preferences.'),
('Wei Zhang', 'wei.zhang@163.com', 'Language Support', 'Cabin crew did not speak Chinese which made communication difficult during the Beijing to Islamabad flight.', 'Provide multilingual staff or translation services for international routes.'),
('James Smith', 'j.smith@gmail.com', 'Punctuality', 'Flight from London to Karachi departed and arrived on time. Appreciate the punctuality and smooth operations.', 'Maintain excellent on-time performance and continue efficient operations.'),
('Maria Garcia', 'maria.garcia@hotmail.com', 'Booking Experience', 'Website is not available in Spanish which made booking difficult for my Mexico City to Lahore trip.', 'Provide website translation in major languages to serve international customers better.'),
('Priya Patel', 'priya.patel@yahoo.in', 'Cultural Sensitivity', 'Appreciate the cultural sensitivity shown by crew during Ramadan. They were very understanding and helpful.', 'Continue training staff on cultural awareness and religious considerations for diverse passengers.'),
('Omar Al-Rashid', 'omar.alrashid@gmail.com', 'Premium Services', 'Business class service from Riyadh to Islamabad exceeded expectations. Excellent hospitality and comfort.', 'Maintain premium service quality and consider introducing first class on major routes.'),
('Anna Van Der Berg', 'anna.vandenberg@email.nl', 'Environmental Concerns', 'Concerned about the environmental impact of flights. Would appreciate more information about your sustainability efforts.', 'Implement and communicate environmental initiatives like carbon offset programs and fuel-efficient aircraft.');
